package REPLHW;

public class Repl48 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		Using while loops that print out even numbers from 1 to 13
//
//		Example Output:
//		2
//		4
//		6
//		8
//		10
//		12
		
		int num=2;
		
		while(num<13) {
			System.out.println(num);
			num+=2;
		}
		
	
	
	
	}

}
