package REPLHW;

public class Repl52 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		Create a do while loop that prints out "Hello World!" 5 times. 
//
//		Output:
//		Hello World!
//		Hello World!
//		Hello World!
//		Hello World!
//		Hello World! 
//	
//	
	
		int num=1;
		
	do {
		System.out.println("Hello World!");
		num++;
	} while (num<=5);
	
	
	
	}

}
