package REPLHW;

public class Repl53 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		Using do while loop print even numbers from 20 to 1
//
//		Expected Output:
//		20
//		18
//		16
//		14
//		12
//		10
//		8
//		6
//		4
//		2
	
	int num=20;
	
	do {
		System.out.println(num);
		num-=2;
	}while(num>=1);
	
	
	
	}

}
