package REPLHW;

public class Repl82 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[][] b= {
				{-5,-2,-3,7},
				{1,-5,-2, 2},
				{1,-2, 3,-4}
			};
		
		int sum=0;
		for(int i=0; i<b.length;i++) {
			for(int k=0;k<b[i].length;i++) {
				sum+=b[1][k];
			}
		}
	
	System.out.println(sum);
	
	
	
	
	
	}

}
