package com.syntax.class01;

public class VariableDeclaration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//create a variable and store value
		//1. declaring a variable and assigning a value 
		boolean b=true;
		// 2: way 1 step ~ declare value
		//         2 step~ assign value
		int i;
		i=10000;
		//declare multiple variables and assign value later
		char x, y, z;
		x='A';
		y='^';
		z='0';
		// We can not have variables with the same name
		// Java will complain
		z='#'; // Reassigning the value
		i=2000;
		double i1=14.1515;
		//you can have the same value in different variable
		int num=100;
		int num1=100;
				
		

	}

}
