package com.syntax.class02;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		String str="Morning";
		
		String address="14120 New brook drive";
		
		String phoneNumber="123-456-7890";
		
		String carMake="Toyota";
		String carColor="Black";
		int carYear=2019;
		String carYear1="2020";
		String carPrice="21,000 USD";
		
		
		String greeting="good morning";
		String greeting1="good morning ";
		// everything matters in string Spacing, Capitals
		System.out.println(carYear1);
		
		String apple="Brian";
		String orange="Quisbert";
		char red='A';
		String blue="falls church";
		String green= "va";
		long black=123-123-123l;
		
		System.out.println(apple);
		System.out.println(orange);
		System.out.println(red);
		System.out.println(blue);
		System.out.println(green);
		System.out.println(black);
		
		
		

	}

}
