package com.syntax.class07;

public class WhileLoopIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	// how to print numbers from one to 50
		int num=1;
		
		while(num<=50) {
			System.out.println(num);
			num++;
		}
		
		int num1=20;
		while (num1<=30) {
			System.out.println(num1);
			num1++;
		}
		System.out.println("*****************");
		
		int num2=5;
		while(num2<=15) {
			System.out.println(num2+" ");
			num2++;
		}
		System.out.println("****************");
		
		// how to print values from 10 to 1
		
		int num3=10;
		while (num3>=1) {
			System.out.println(num3);
			num3--;
		}
		
		System.out.println("**********");
		// print from 50 to 1
		//print odd from 1 to 20
		
	int num4=50;
	while (num4>=1) {
		System.out.println(num4);
		num4--;
	}
	System.out.println("***********");
	
	int num5=1;
	
	while(num5<=20) {
		System.out.println(num5);
		num5+=2;
	}
	
	
	
	
	
	
	
	
	
	}

}
