package com.syntax.class09;

public class Patterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int a = 1; a <= 10; a++) {

			for (int j = 1; j <= 10; j++) {
				System.out.print("*");
			}
			System.out.println();

		}

		System.out.println("---------------------------------------------------");
		for(int a=1;a<=5;a++) {
			
			for(int i=1;i<=5;i++) {
				System.out.print(i);
			}
			
			
			System.out.println();
			
		}
		
}
	
	
	}


