package com.syntax.class09;

public class Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int a = 1; a <= 4; a++) {

			for (int b = 1; b <= a; b++) {
				System.out.print("*");
			}

			System.out.println();
		}

	//TRIANGLE OF NUMBERS
	
	for(int a=1;a<=4;a++) {
		for(int b=1;b<=a;b++) {
			System.out.print(b);
		}
		System.out.println();
		
	}
	
	
	
	
	}

}
